from helper.uriHelper import UriHelper, UriHelperValue


class TestUriHelperValue:
    @pytest.mark.parametrize(
        "uri, expected",
        [
            ("https://www.example.com/image.jpg", True),
            ("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAABCAQAAAC1HAwCAAAAC0lEQVR42mNkYAAAAAYAAjCB0C8AAAAASUVORK5CYII=", True),
            ("ftp://ftp.example.com/file.txt", True),
            ("file:///path/to/file.txt", True),
            ("invalid_uri", False),
            ("", False),
        ],
    )
    def test_is_uri_only(self, uri, expected):
        assert UriHelperValue.is_uri_only(uri) == expected

class TestUriHelper:

    @pytest.mark.parametrize(
        "uri, expected",
        [
            ("https://www.example.com/image.jpg", True),
            ("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAABCAQAAAC1HAwCAAAAC0lEQVR42mNkYAAAAAYAAjCB0C8AAAAASUVORK5CYII=", False), # filenameがないためFalse
            ("data:image/jpeg;filename=my_image.jpg;base64,", True),
            ("data:image/jpeg;filename=my_image;base64,", True),
            ("data:image/jpeg;base64,", False),
            ("ftp://ftp.example.com/file.txt", True),
            ("file:///path/to/file.txt", True),
        ],
    )
    def test_is_enable_filename(self, uri, expected):
      helper = UriHelper(uri)
      assert helper.is_enable_filename() == expected

    @pytest.mark.parametrize(
        "uri, expected",
        [
            ("https://www.example.com/image.jpg", "image"),
            ("data:image/jpeg;base64,", None), # filenameがないData URI
            ("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAABCAQAAAC1HAwCAAAAC0lEQVR42mNkYAAAAAYAAjCB0C8AAAAASUVORK5CYII=", None),
            ("data:image/png;filename=my_image.png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAABCAQAAAC1HAwCAAAAC0lEQVR42mNkYAAAAAYAAjCB0C8AAAAASUVORK5CYII=", "my_image"),  # filenameがあるData URI
            ("data:image/png;filename=my_image;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAABCAQAAAC1HAwCAAAAC0lEQVR42mNkYAAAAAYAAjCB0C8AAAAASUVORK5CYII=",  "my_image"),  # filenameがあるData URI
            ("ftp://ftp.example.com/file.txt", "file"),
            ("file:///path/to/file.txt", "file"),
            ("https://www.example.com/", None), # ファイル名がないURI
            ("https://www.example.com/path/to/",None), # ファイル名がないURI

        ],
    )
    def test_get_filename(self, uri, expected):
        helper = UriHelper(uri)
        assert helper.get_filename() == expected

    @pytest.mark.parametrize(
        "uri, expected",
        [
            ("https://www.example.com/image.jpg", ".jpg"),
            ("https://www.example.com/path/to/image.png", ".png"),
            ("data:image/jpeg;base64,", ".jpg"),  # 拡張子があるData URI
            ("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAABCAQAAAC1HAwCAAAAC0lEQVR42mNkYAAAAAYAAjCB0C8AAAAASUVORK5CYII=", ".png"),  # 拡張子があるData URI
            ("data:application/octet-stream;base64,SGVsbG8=",None), # 拡張子がないData URI
            ("ftp://ftp.example.com/file.txt", ".txt"),
            ("file:///path/to/file",None), # 拡張子がないURI
            ("https://www.example.com/", None),# 拡張子がないURI
            ("https://www.example.com/path/to/",None),# 拡張子がないURI
        ],
    )
    def test_get_ext(self, uri, expected):
        helper = UriHelper(uri)
        assert helper.get_ext() == expected

    def test_uri_helper_value_valid(self):
        uri = "https://www.example.com"
        value_object = UriHelperValue(uri)
        assert value_object.uri == uri

    def test_uri_helper_value_invalid(self):
        with pytest.raises(ValueError):
            UriHelperValue("invalid uri")

    def test_uri_helper_value_none(self):
        with pytest.raises(ValueError):
            UriHelperValue(None)

    def test_uri_helper_invalid_type(self):
        with pytest.raises(ValueError):
            UriHelper(123)
